function descargar_textos() {
    const { jsPDF } = window.jspdf;
    var doc = new jsPDF();

    const margen = 20;
    const anchoPagina = doc.internal.pageSize.width;
    const altoPagina = doc.internal.pageSize.height;
    const imgLogo = ' data:image/png;base64,';
    const scrib_logo =  'data:image/png;base64,';
     // Tama√±o del logo original en p√≠xeles
     const anchoLogoOriginal = 247;
     const altoLogoOriginal = 476;
 
     // Tama√±o m√°ximo del logo en el PDF (en mm)
     const anchoLogoMaximo = 10; // aproximadamente 50 mm
 
     // Calcular el alto proporcional del logo
     const altoLogo = altoLogoOriginal * (anchoLogoMaximo / anchoLogoOriginal);
 
     // Calcular la posici√≥n x del logo para que est√© a la derecha
     const xLogo = anchoPagina - anchoLogoMaximo - margen + 15;
    // Tama√±o del segundo logo (scrib_logo)
    const anchoScribLogoOriginal = 2534;
    const altoScribLogoOriginal = 584;
    const anchoScribLogoMaximo = 60;
    const altoScribLogo = altoScribLogoOriginal * (anchoScribLogoMaximo / anchoScribLogoOriginal);
    const xLogoIzquierda = margen - 27;

     function agregarLogoEnPagina() {
         // A√±adir el logo en la esquina superior derecha de cada p√°gina
         doc.addImage(imgLogo, 'PNG', xLogo, margen - 17, anchoLogoMaximo, altoLogo);
         doc.addImage(scrib_logo, 'PNG', xLogoIzquierda, margen - 13, anchoScribLogoMaximo, altoScribLogo);

     }
 
    function agregarTextoEnPagina(texto, x, yInicial, tamano, color, bold = false) {
        doc.setFontSize(tamano);
        doc.setTextColor(color[0], color[1], color[2]);
        doc.setFont('Times-Roman', bold ? 'bold' : 'normal');

        let y = yInicial;
        const lineas = doc.splitTextToSize(texto, anchoPagina - 2 * margen);

        lineas.forEach(linea => {
            if (y > altoPagina - margen) {
                doc.addPage();
                agregarLogoEnPagina();
                y = margen + 10; // Ajustar seg√∫n el tama√±o del logo
            }
            doc.text(linea, x, y);
            y += 6; // Ajustar seg√∫n el espacio deseado entre l√≠neas
        });

        return y;
    }

    agregarLogoEnPagina(); // A√±adir el logo en la primera p√°gina

    // Configuraci√≥n y adici√≥n de texto para la primera parte
    let yActual = agregarTextoEnPagina(val_nombre1, margen, margen + 13, 25, [0, 0, 0], true);

    // Crear un div temporal
    var div = document.createElement("div");

    // Asignar el contenido a innerHTML del div temporal
    div.innerHTML = texto_guardado1;

    yActual = agregarTextoEnPagina(div.textContent, margen, yActual + 5, 15, [0, 0, 0]);

    // Descargar el PDF
    doc.save(val_nombre1 + '.pdf');
    // Nueva p√°gina para el segundo conjunto de textos
    doc = new jsPDF();

    agregarLogoEnPagina();

    // Configuraci√≥n y adici√≥n de texto para la segunda parte
    yActual = agregarTextoEnPagina(val_nombre2, margen, margen + 13, 25, [0, 0, 0], true);

    div.innerHTML = texto_guardado2;

    yActual = agregarTextoEnPagina(div.textContent, margen, yActual + 5, 15, [0, 0, 0]);

    // Descargar el PDF
    doc.save(val_nombre2 + '.pdf');
}

